$color-20: #660708;
$color-30: #a4161a;
$color-40: #ba181b;
$color-50: #e5383b;
$color-60: #b1a7a6;
$color-70: #d3d3d3;
$color-80: #f5f3f4;
$color-90: #fff;

* {
    font-family: "Mulish", sans-serif;
    margin: 0;
    padding: 0;
}

a {
    text-decoration: none;
    color: $color-0;
}

.nav {
    background-color: $color-60;
    display: flex;
    align-items: center;
    justify-content: space-around;

    &__select {
        color: $color-0;
        display: flex;
        align-items: center;
        
        &__item {
            margin: 18px;
            font-size: 24px;
            padding: 6px;
        }
    }

    &__menu {
        color: $color-0;
        display: flex;
        align-items: center;
        list-style-type: none;
        

        &__item {
            margin: 20px;
            font-size: 24px;
        }
    }
}

.week__days {
    margin: 24px 0;
    h1 {
        color: $color-10;
        text-align: center;
        margin-bottom: 24px;
    }
    .current__days {
        display: flex;
        justify-content: center;
        align-items: center;
        .current__day {
            width: 100px;
            height: 80px;
            background-color: #a4161a;
            color: $color-80;
            margin: 0 10px;
            text-align: center;
        }
    }
}
.schedule {
    width: 100%;
    display: flex;
    align-items: center;
    justify-content: center;
    flex-direction: column;
    margin-bottom: 48px;
    &__caption {
        margin: 24px 0;
        h1 {
            text-align: center;
            color: $color-10;
        }
    }
    &__container {
        display: flex;
        flex-direction: column;
        width: 90%;

        &__item {
            display: flex;
            width: 100%;
            border: 1px solid $color-10;
        }

        &__time {
            width: 10%;
            border: 1px solid $color-10;
            display: flex;
            justify-content: center;
            align-items: center;
            font-size: 16px;
        }

        &__subgroup {
            width: 45%;
            display: flex;
            flex-direction: column;
            align-items: center;
            justify-content: center;
            border: 1px solid $color-10;
            padding: 10px;
            box-sizing: border-box;

            &--full {
                width: 90%;
            }

            &__title {
                font-size: 24px;
                font-weight: bold;
            }

